import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Validators } from "@angular/forms";
export interface Food {
  value: string;
  display: string;
}
@Component({
  selector: 'app-test-tab',
  templateUrl: './test-tab.component.html',
  styleUrls: ['./test-tab.component.scss']
})
export class TestTabComponent {

  title = "AngularApp";
    firstFormGroup!: FormGroup;
    secondFormGroup!: FormGroup;
    firstFormGroup1!: FormGroup;
    secondFormGroup1!: FormGroup;
    constructor(private _formBuilder: FormBuilder) { }
    ngOnInit() {
        this.firstFormGroup = this._formBuilder.group({
            firstCtrl: ["", Validators.required]
        });
        this.secondFormGroup = this._formBuilder.group({
            secondCtrl: ["", Validators.required]
        });
        this.firstFormGroup1 = this._formBuilder.group({
            firstCtrl1: ["", Validators.required]
        });
        this.secondFormGroup1 = this._formBuilder.group({
            secondCtrl1: ["", Validators.required]
        });
    }
}
<div class="card-body">
	<mat-horizontal-stepper #stepper linear>
		<mat-step [stepControl]="firstFormGroup">
			<form [formGroup]="firstFormGroup">
				<ng-template matStepLabel>
					Basic Details
				</ng-template>
				<mat-form-field>
                    <input matInput placeholder="Last name, First name"
						formControlName="firstCtrl" required />
                    <div class="card-body">
                        <div class="tab-content" id="onboard-tab-ontent">
                    <app-basic-details-form></app-basic-details-form>
                    </div>
                    </div>


				</mat-form-field>
				<div>
					<button mat-button matStepperNext>
						Next
					</button>
				</div>
			</form>
		</mat-step>
		<mat-step [stepControl]="secondFormGroup">
			<form [formGroup]="secondFormGroup">
				<ng-template matStepLabel>
					System Access Request
				</ng-template>
				<mat-form-field>
					<input matInput placeholder="Address"
						formControlName="secondCtrl" required />
				</mat-form-field>
				<div>
					<button mat-button matStepperPrevious>
						Back
					</button>
					<button mat-button matStepperNext>
						Next
					</button>
				</div>
			</form>
		</mat-step>
		<mat-step>
			<ng-template matStepLabel>
				Done
			</ng-template>
			Details taken.
			<div>
				<button mat-button matStepperPrevious>
					Back
				</button>
				<button mat-button (click)="stepper.reset()">
					Reset
				</button>
			</div>
		</mat-step>
	</mat-horizontal-stepper>
</div>
