import { Employee } from '@/module/employee';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-onboard-forms-tab',
  templateUrl: './onboard-forms-tab.component.html',
  styleUrls: ['./onboard-forms-tab.component.scss']
})
export class OnboardFormsTabComponent implements OnInit {
  activeTab: string = 'basic-details-tab';
  employee: Employee = new Employee();

  // Method to handle tab changes
  setActiveTab(tabId: string) {
    alert(tabId)
    this.activeTab = tabId;
  }

  ngOnInit() {
  }

}
<!-- Content Header (Page header) -->
<section class="content-header">
    <!-- ... Header content ... -->
</section>

<!-- Tabbed Interface with Animation -->
<section class="content">
    <div class="container-fluid">
        <div class="row">
            <div class="col-md-12">
                <div class="card card-primary card-outline card-tabs">
                    <div class="card-header p-0 border-bottom-0 text-center">
                        <ul class="nav nav-pills justify-content-center" id="onboard-tab" role="tablist">
                            <!-- Add data-target attribute to anchor tags for animation -->
                            <li class="nav-item">
                                <a class="nav-link" data-toggle="pill" data-target="#basic-details" role="tab" aria-controls="basic-details" aria-selected="true">Basic Details <i class="fas fa-user"></i></a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link active" data-toggle="pill" data-target="#system-access" role="tab" aria-controls="system-access" aria-selected="true">System Access Request <i class="fas fa-lock"></i></a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" data-toggle="pill" data-target="#application-user-access" role="tab" aria-controls="application-user-access" aria-selected="false">Application User Access <i class="fas fa-shield-alt"></i></a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" data-toggle="pill" data-target="#other-support" role="tab" aria-controls="other-support" aria-selected="false">Other Support Tool <i class="fas fa-cogs"></i></a>
                            </li>
                        </ul>
                    </div>
                    <div class="card-body">
                        <div class="tab-content" id="onboard-tab-content">
                            <div class="tab-pane fade" id="basic-details" role="tabpanel">
                                <app-basic-details-form></app-basic-details-form>
                            </div>
                            <div class="tab-pane fade" id="system-access" role="tabpanel">
                                <app-system-access-form></app-system-access-form>
                            </div>
                            <div class="tab-pane fade" id="application-user-access" role="tabpanel">
                                <app-application-user-access-form></app-application-user-access-form>
                            </div>
                            <div class="tab-pane fade" id="other-support" role="tabpanel">
                                <app-other-support-form></app-other-support-form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</section>

<!-- Main content -->
<section class="content">

</section>

<!-- Add jQuery and Bootstrap -->
<script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
<script src="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>

<!-- Custom JavaScript for tab animation -->
<script>
    $(document).ready(function(){
        $('a[data-toggle="pill"]').on('shown.bs.tab', function (e) {
            var target = $(e.target).attr("data-target");
            if (target) {
                $(target).addClass('show');
            }
        });
        $('a[data-toggle="pill"]').on('hidden.bs.tab', function (e) {
            var target = $(e.target).attr("data-target");
            if (target) {
                $(target).removeClass('show');
            }
        });
    });
</script>
