import { Employee } from '@/module/employee';
import { Component, OnInit } from '@angular/core';
import {FormBuilder, FormGroup, Validators} from '@angular/forms';
import {STEPPER_GLOBAL_OPTIONS} from '@angular/cdk/stepper';
import { GenericValidator } from "@/validation/generic-validator";

@Component({
  selector: 'app-basic-details-form',
  templateUrl: './basic-details-form.component.html',
  styleUrls: ['./basic-details-form.component.scss'],
  providers: [{
    provide: STEPPER_GLOBAL_OPTIONS, useValue: {showError: true}
  }]
})
export class BasicDetailsFormComponent implements OnInit{
  employee: Employee = new Employee();
  form: FormGroup;

  constructor(private formBuilder: FormBuilder) {

  }

  ngOnInit() {
    this.form = this.formBuilder.group({
      epf_no: ['', [GenericValidator.epf_no]],
      company: ['', [GenericValidator.company]],
      branch: ['', [GenericValidator.branch]],
      department: ['', [GenericValidator.department]],
      designation: ['', [GenericValidator.designation]],
      grade: ['', [GenericValidator.g]],
      date_of_joining: ['', [GenericValidator.epf_no]],
      hod_name: ['', [GenericValidator.epf_no]],
      epf_no_of_hod: ['', [GenericValidator.epf_no]],
    });
  }

}
