import { Component } from '@angular/core';

@Component({
  selector: 'app-common-success',
  templateUrl: './common-success.component.html',
  styleUrls: ['./common-success.component.scss']
})
export class CommonSuccessComponent {

}
<html>
  <head>
    <link href="https://fonts.googleapis.com/css?family=Nunito+Sans:400,400i,700,900&display=swap" rel="stylesheet">
  </head>
    <style>
      body {
        text-align: center;
        padding: 40px 0;
        background: #EBF0F5;
      }
        h1 {
          color: #88B04B;
          font-family: "Nunito Sans", "Helvetica Neue", sans-serif;
          font-weight: 900;
          font-size: 40px;
          margin-bottom: 10px;
        }
        p {
          color: #404F5E;
          font-family: "Nunito Sans", "Helvetica Neue", sans-serif;
          font-size:20px;
          margin: 0;
        }
      i {
        color: #9ABC66;
        font-size: 100px;
        line-height: 200px;
        margin-left:-15px;
      }
      .card {
        background: white;
        padding: 60px;
        border-radius: 4px;
        box-shadow: 0 2px 3px #C8D0D8;
        display: inline-block;
        margin: 0 auto;
      }
    </style>
    <body>
      <div class="card">
      <div style="border-radius:200px; height:200px; width:200px; background: #F8FAF5; margin:0 auto;">
        <i class="checkmark">✓</i>
      </div>
        <h1>Success</h1> 
        <p>We received your purchase request;<br/> we'll be in touch shortly!</p>
      </div>
      <div class="row">
        <div class="col-md-6 form-action text-right">
            <button mat-button matStepperPrevious class="btn btn-primary btn-rounded">
                <mat-icon>arrow_back</mat-icon> Back
            </button>
        </div>
        <div class="col-md-6 form-action text-left">
            <button mat-button (click)="stepper.reset()" class="btn btn-primary btn-rounded">
                Save <mat-icon>done_all</mat-icon>
            </button>
        </div>
    </div>
    </body>
</html>
