import { Component } from '@angular/core';

@Component({
  selector: 'app-onboard-forms-tab',
  templateUrl: './onboard-forms-tab.component.html',
  styleUrls: ['./onboard-forms-tab.component.scss']
})
export class OnboardFormsTabComponent {
  activeTab: string = 'ims-user-tab'; // Initialize with the ID of the default tab

  // Method to handle tab changes
  setActiveTab(tabId: string) {
    this.activeTab = tabId;
  }

}
